mapper.select:
select

from mobile_detail
where 1 = 1


      and id = #{record.id}


      and name = #{record.name}


      and brand = #{record.brand}


      and platform = #{record.platform}


      and os_version = #{record.osVersion}


      and screen_size = #{record.screenSize}


      and device_id = #{record.deviceId}


      and client_id = #{record.clientId}


      and udid = #{record.udid}


      and slave_id = #{record.slaveId}


      and status = #{record.status}


      and ready_state = #{record.readyState}


      and device_type = #{record.deviceType}


limit #{limit}
offset #{offset}

select count(id)
from mobile_detail
where 1 = 1


  and id = #{record.id}


  and name = #{record.name}


