mapper.select:
select

from project_info
where 1 = 1


  and id = #{record.id}


  and name = #{record.name}


  and gitAddress = #{record.gitAddress}


  and describe = #{record.describe}


  and status = #{record.status}


  and operator = #{record.operator}


  and iosDeveloper = #{record.iosDeveloper}


  and androidDeveloper = #{record.androidDeveloper}


  and lastPullTime = #{record.lastPullTime}


  and createTime = #{record.createTime}


  and updateTime = #{record.updateTime}


limit #{limit}
offset #{offset}

select
count(id)
from project_info
where 1 = 1


    and id = #{record.id}


    and name = #{record.name}


    and gitAddress = #{record.gitAddress}


    and describe = #{record.describe}


    and status = #{record.status}


    and operator = #{record.operator}


    and iosDeveloper = #{record.iosDeveloper}


    and androidDeveloper = #{record.androidDeveloper}


    and lastPullTime = #{record.lastPullTime}


    and createTime = #{record.createTime}


    and updateTime = #{record.updateTime}
